     @Test
     fun getDataFromRemote() {

         Mockito.`when`(repository.getArtistsNameList("eminem")).thenReturn(Observable.just(getObject()))

         val testObserver = TestObserver<ArtistsNameList>()

         viewModel.generateArtistsListApiCall("eminem")
             .subscribe(testObserver)

         testObserver.assertNoErrors()
         testObserver.assertValue { responseData -> responseData.total == 20 }
     }





